import React , {useState    } from 'react'
import axios from 'axios'
export default function Main() {
     const [value,setValue] = useState({
         name:"" , 
         title :"" ,
         attchment: []
     })
      
    const submit = (e)=>{
        console.log(value)
        
        const formdata = new FormData()
        formdata.append("name" ,value.name)
        formdata.append("title" ,value.title)
        formdata.append("file" ,value.attchment)
         
         
      axios.post('http://localhost:5000/submitform' , formdata ).then((res)=>{
           console.log(" response  " , res )
      }).catch(err=>{
        console.log("Error : " , err)
           
      })
        
         
    }
    
    const onChangeHandler = (e)=>{
        debugger
        console.log(e.target.name)
          
        if(e.target.name=="attchment") {
              setValue( { ...value , [e.target.name] : e.target.files[0] }  )
               return 
            }
         
        setValue({ ...value ,  [e.target.name] : e.target.value } );
         console.log(value)
        }


    return (
        <React.Fragment>
   
                 <input  type="text" value={value.name} onChange={onChangeHandler} name="name"   /> <br /> 
                 <input  type="text" value={value.title} onChange={onChangeHandler}     name="title"  /> <br /> 
                
                   
                 select File <input type="file"   name="attchment"  onChange={onChangeHandler}   /> 
                  <input type="submit" onSubmit={submit}   value="submit" />
        
        </React.Fragment>
    )
}
