import React , {useState    } from 'react'

export default function Main() {
     const [value,setValue] = useState({
         name:"" , 
         title :"" ,
         attachments: []
     })
      
    const submit = (e)=>{
        console.log(value)
        
        const formdata = new FormData()
        formdata.append("name" ,value.name)
        formdata.append("title" ,value.title)
        formdata.append("title" ,value.title)
        
    
        
         
    }
    
    const onChangeHandler = (e)=>{
         
        if(e.target.name=="file") {
          
              setValue({attachments:e.target.files[0]})
            
        }
         
        setValue({ ...value ,  [e.target.name] : e.target.value } );
         console.log(value)
    
        }


    return (
        <React.Fragment>
   
                 <input  type="text" value={value.name} onChange={onChangeHandler} name="name"   /> <br /> 
                 <input  type="text" value={value.title} onChange={onChangeHandler}     name="title"  /> <br /> 
                
                   
                 select File <input type="file"   name="file"  onChange={onChangeHandler} /> 
                  <input type="submit" onSubmit={submit}   value="submit" />
        
        </React.Fragment>
    )
}
