import React ,{useState} from "react";
import { Button } from "react-bootstrap";
import { Modal , Form , Row ,Col } from "react-bootstrap";


export default function logsignup() {
  const [value,setValue] = useState({
         name:"" , 
         title :"" ,
         attchment: [] ,
         video :[]
     })

     const [err , setError] = useState({
          setName:"" , 
          setTitle : "" 
     })
      
    const submit = (e)=>{
        debugger
        console.log(value) 
     
        const model ={ 
            
              name : value.name , 
              title: value.title   
            }
                                    
          if(value.name!="" && value.title!="" ){
                        axios.post('submitform/' , model ).then((res)=>{
                        console.log(" response " , res.data )
                        if(res.data.status==true){
                         const formdata = new FormData()

                            for(let i=0; i<value.attchment.length ; i++){
                                formdata.append('attchment' , value.attchment[i] )
                                axios.post('/addFile' , formdata, 
                                     {
                                 headers : { 
                                        "Content-Type": "multipart/form-data"
                                      }} )
                                       .then(res=>{
                                     console.log(res.data.message)
                                 })    
                             }

                         
                            alert(`name  ${res.data.name} and title ${res.data.title} saved `)           
                            
                        }
                            else{
                            alert(` error `)
                            }
                    }).catch(err=>{
                        console.log("Error : "  , err)
                    })
    }
    else{
         
        if(value.name==""){
             setError({setName:" please enter name "})
        }
        if(value.title==""){
             setError({setTitle:"please Enter title  "})
            }

     if( value.name=="" && value.title=="" ){
         
        setError({setName:"please enter name "})
        setError({setTitle:" please Enter title " }) 

            }
     
        }     
      
     
    }
  return (
    <React.Fragment>
     
    <Button variant=" outline-success "  onClick={() => setModalShow(true)}>
        Login/SignUp
      </Button>

      <MyVerticallyCenteredModal
        show={modalShow}
        onHide={() => setModalShow(false)}
      />

    </React.Fragment>
  )
}


export  function MyVerticallyCenteredModal(props) {
  return (
     <React.Fragment>
    <Modal
      {...props}
      size="md"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          Login/SignUp
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
         
  
<Form>
<Form.Group as={Row} className="mb-3" controlId="formPlaintextPassword" >
    <Form.Label column sm="4"     >
      Email
    </Form.Label>

    <Col sm="8">
      <Form.Control type="text" value={value.name} onChange={onChangeHandler} name="name"   />
    </Col>

  </Form.Group>

  <Form.Group as={Row} className="mb-3" controlId="formPlaintextPassword">
    <Form.Label column sm="4">
      Password
    </Form.Label>

    <Col sm="8">
      <Form.Control type="password"  placeholder="Password" value={value.title} onChange={onChangeHandler}     name="title"  />
    </Col>

  </Form.Group>
</Form>


      </Modal.Body>
      <Modal.Footer>
        <Button onClick={props.onHide}>Close</Button>
      </Modal.Footer>
    </Modal>
    </React.Fragment>

  );
}
